def calcular_peso_total(nodo):
    # 1. Caso Base: Es un archivo
    if nodo["tipo"] == "file":
        return nodo["size"]

    # 2. Caso Recursivo: Es una carpeta
    total = 0
    for nombre_hijo, info_hijo in nodo["contenido"].items():
        total += calcular_peso_total(info_hijo)
    return total
