def analizar_ventas(black_friday_ids, cyber_monday_ids):
    conjunto_bf = set(black_friday_ids)
    conjunto_cm = set(cyber_monday_ids)

    return {
        "total_unicos": conjunto_bf | conjunto_cm,       # Unión
        "clientes_leales": conjunto_bf & conjunto_cm,    # Intersección
        "solo_black_friday": conjunto_bf - conjunto_cm   # Diferencia
    }
