def integracion_monte_carlo(n_puntos):
    a, b, y_max = 0, math.pi, 1.0
    debajo = 0
    for _ in range(n_puntos):
        x = random.uniform(a, b)
        y = random.uniform(0, y_max)
        if y <= math.sin(x):
            debajo += 1
    return (b-a) * y_max * (debajo / n_puntos)
