def estado_radar(h, k, r, x, y):
    # Calculamos la distancia al cuadrado para evitar la raíz cuadrada (optimización)
    distancia_cuadrada = (x - h)**2 + (y - k)**2
    radio_cuadrado = r**2

    if distancia_cuadrada < radio_cuadrado:
        return "DENTRO"
    elif distancia_cuadrada > radio_cuadrado:
        return "FUERA"
    else:
        return "BORDE"
