actividad_practica.codigo_plantilla:
#include <stdio.h>

int main() {
    int filasA, colA, filasB, colB;

    printf("Dimensiones de A (filas columnas): ");
    scanf("%d %d", &filasA, &colA);

    printf("Dimensiones de B (filas columnas): ");
    scanf("%d %d", &filasB, &colB);

    if (colA != filasB) {
        printf("No se pueden multiplicar: colA != filasB\n");
        return 1;
    }

    int A[50][50], B[50][50], C[50][50];

    printf("Ingresa A:\n");
    for (int i = 0; i < filasA; i++)
        for (int j = 0; j < colA; j++)
            scanf("%d", &A[i][j]);

    printf("Ingresa B:\n");
    for (int i = 0; i < filasB; i++)
        for (int j = 0; j < colB; j++)
            scanf("%d", &B[i][j]);

    // Multiplicación
    for (int i = 0; i < filasA; i++) {
        for (int j = 0; j < colB; j++) {
            C[i][j] = 0;
            for (int k = 0; k < colA; k++) {
                C[i][j] += A[i][k] * B[k][j];
            }
        }
    }

    printf("\nResultado C = A * B:\n");
    for (int i = 0; i < filasA; i++) {
        for (int j = 0; j < colB; j++)
            printf("%d ", C[i][j]);
        printf("\n");
    }

    return 0;
}
