// figuras.h
#ifndef FIGURAS_H
#define FIGURAS_H

double area_circulo(double r);
double area_triangulo(double base, double altura);
double area_rectangulo(double base, double altura);

#endif

// figuras.c
#include "figuras.h"
#define PI 3.14159

double area_circulo(double r) { return PI * r * r; }
double area_triangulo(double base, double altura) { return (base * altura) / 2; }
double area_rectangulo(double base, double altura) { return base * altura; }

// main.c
#include <stdio.h>
#include "figuras.h"

int main() {
    printf("Área círculo (r=5): %.2f\n", area_circulo(5));
    printf("Área triángulo (3,4): %.2f\n", area_triangulo(3,4));
    printf("Área rectángulo (2,6): %.2f\n", area_rectangulo(2,6));
}

// Compilación sugerida:
// gcc main.c figuras.c -o figuras
