6_reto_proyecto.solucion_referencia:
#include <stdio.h>
#include <stdlib.h>

void modificar_arreglo(int *arr, int n) {
    for (int i = 0; i < n; i++) {
        *(arr + i) *= 2;
    }
}

int main() {
    int n;
    printf("Ingrese el tamaño del arreglo: ");
    scanf("%d", &n);

    int *arr = malloc(n * sizeof(int));
    if (arr == NULL) {
        printf("Error de memoria\n");
        return 1;
    }

    for (int i = 0; i < n; i++) {
        printf("Valor[%d]: ", i);
        scanf("%d", &arr[i]);
    }

    modificar_arreglo(arr, n);

    printf("Arreglo modificado:\n");
    for (int i = 0; i < n; i++) {
        printf("%d ", arr[i]);
    }

    free(arr);
    return 0;
}
