actividad_practica.solucion_referencia:
#include <stdio.h>
#include <dirent.h>
#include <string.h>

void recorrer(const char *ruta) {
    DIR *d = opendir(ruta);
    if (!d) return;

    struct dirent *dir;
    while ((dir = readdir(d)) != NULL) {
        if (strcmp(dir->d_name, ".") == 0 || strcmp(dir->d_name, "..") == 0)
            continue;

        char path[512];
        sprintf(path, "%s/%s", ruta, dir->d_name);

        printf("%s\n", path);

        if (dir->d_type == DT_DIR) {
            recorrer(path);
        }
    }
    closedir(d);
}
