#include <stdio.h>

void decimal_a_binario(int n) {
    int bits[32];
    int i = 0;
    if (n == 0) { printf("Binario: 0\n"); return; }

    while (n > 0) {
        bits[i++] = n % 2;
        n /= 2;
    }

    printf("Binario: ");
    for (int j = i - 1; j >= 0; j--) printf("%d", bits[j]);
    printf("\n");
}

void decimal_a_hex(int n) {
    char hex[32];
    int i = 0;
    char tabla[] = "0123456789ABCDEF";

    if (n == 0) { printf("Hex: 0\n"); return; }

    while (n > 0) {
        hex[i++] = tabla[n % 16];
        n /= 16;
    }

    printf("Hex: ");
    for (int j = i - 1; j >= 0; j--) printf("%c", hex[j]);
    printf("\n");
}
