6_reto_proyecto.solucion_referencia:
class Libro:
    def __init__(self, titulo, autor):
        self.titulo = titulo
        self.autor = autor
        self.disponible = True

class Usuario:
    def __init__(self, nombre):
        self.nombre = nombre
        self.libros = []

    def limite_prestamos(self):
        raise NotImplementedError

class Estudiante(Usuario):
    def limite_prestamos(self):
        return 3

class Profesor(Usuario):
    def limite_prestamos(self):
        return 10

class Biblioteca:
    def __init__(self):
        self.catalogo = []

    def prestar(self, libro, usuario):
        if not libro.disponible:
            return f"El libro '{libro.titulo}' no está disponible."
        if len(usuario.libros) >= usuario.limite_prestamos():
            return f"{usuario.nombre} alcanzó su límite de préstamos."
        libro.disponible = False
        usuario.libros.append(libro)
        return f"Libro '{libro.titulo}' prestado a {usuario.nombre}."

    def devolver(self, libro, usuario):
        if libro not in usuario.libros:
            return f"{usuario.nombre} no tiene este libro."
        libro.disponible = True
        usuario.libros.remove(libro)
        return f"Libro '{libro.titulo}' devuelto por {usuario.nombre}."
