# Implementación simple
class Numero:
    def __init__(self, valor): self.valor = valor

class BinOp:
    def __init__(self, op, izq, der):
        self.op = op; self.izq = izq; self.der = der

class Parser:
    def __init__(self, tokens):
        self.tokens = tokens
        self.pos = 0

    def consumir(self):
        tok = self.tokens[self.pos]
        self.pos += 1
        return tok

    def ver(self):
        return self.tokens[self.pos]

    def expr(self):
        nodo = self.term()
        while self.pos < len(self.tokens) and self.ver() == '+':
            self.consumir()
            nodo = BinOp('+', nodo, self.term())
        return nodo

    def term(self):
        nodo = self.factor()
        while self.pos < len(self.tokens) and self.ver() == '*':
            self.consumir()
            nodo = BinOp('*', nodo, self.factor())
        return nodo

    def factor(self):
        tok = self.consumir()
        if tok.isdigit():
            return Numero(int(tok))
        elif tok == '(':
            nodo = self.expr()
            self.consumir()  # ')'
            return nodo


def eval_ast(nodo):
    if isinstance(nodo, Numero): return nodo.valor
    if nodo.op == '+': return eval_ast(nodo.izq) + eval_ast(nodo.der)
    if nodo.op == '*': return eval_ast(nodo.izq) * eval_ast(nodo.der)

# Ejemplo
p = Parser(list("3+4*2"))
ast = p.expr()
print(eval_ast(ast))  # 11
